/*
* This function can be copied into JavaScript to
* use a bottom snackbar. You remove it by setting:
* snackbar.style.opacity='0'
*/

var createSnackbar=function(message){
	if ($('#snackbarstyle').length){
		$('#snackbarstyle').remove();
	};
	var style = document.createElement('style');
	style.id = "snackbarstyle";
	style.innerHTML = atob('');
	document.head.appendChild(style);
	if ($('#snackbar').length){
		$('#snackbar').remove();
	};
	var snackbar = document.createElement('div');
	snackbar.id = "snackbar";
	snackbar.innerText = message;
	snackbar.addEventListener('transitionend', function() {
		snackbar.remove();
		style.remove();
	});
	document.body.appendChild(snackbar);
	
	snackbar.setAttribute('class','show');
	return snackbar;
};